import pandas as pd
import numpy as np
import matplotlib.pyplot as plt

def read(file):
    df=pd.read_csv(file)
    return df.values

X=read("/home/divyakshi/Documents/Longitude.csv")
Y=read("/home/divyakshi/Documents/Latitude.csv")
X=X.reshape((19,))
Y=Y.reshape((19,))

'''X=(X-X.mean())/(X.std())
Y=(Y-Y.mean())/(Y.std())'''
plt.scatter(X,Y)
#plt.grid(True)

mx=np.max(X)
minx=np.min(X)
my=np.max(Y)
miny=np.min(Y)
Ax=[]
Ay=[]
c=0
initialx = X[0] + 0.025
fx = X[0]
W = []
while initialx<=mx+0.025:
    fy = Y[0]
    initialy=Y[0]+0.025
    while initialy<=my+0.025:
        By=[]
        Bx=[]
        w=0
        for j in range(19):
            if initialx > X[j] >= fx and initialy > Y[j] >= fy:
                By.append((Y[j]))
                Bx.append((X[j]))
                w+=1
        Ay.append(By)
        Ax.append(Bx)
        if w!=0:
            W.append(w)
        c+=1
        fy=initialy
        initialy += 0.025
    fx = initialx
    initialx += 0.025

print(Ax)
print(Ay)
plt.show()
def average(Ax):
    Avx=[]
    for x in Ax:
        if x!=[]:
            x=np.asarray(x)
            Avx.append(np.mean(x))
    return Avx
Avx=average(Ax)
Avy=average(Ay)
print(Avx)
print(Avy)
print(W)
for i in range(len(W)):
    if W[i]<=1:
        plt.scatter(Avx[i],Avy[i],color='green')
    else:
        plt.scatter(Avx[i],Avy[i],color='red')
plt.grid(True)
plt.show()